//该文件用于创建Vuex中最为核心的store

//引入Vuex
import Vuex from 'vuex'

//引入Vue
import Vue from 'vue'

//应用Vuex插件
Vue.use(Vuex)
//准备actions--用于响应组件中的动作
const actions = {
    jia:function(a,b){
        console.log('actions中的jia被调用了',a,b)
    }

}

//准备mutations--用于操作数据（state）
const mutations = {}

//准备state--用于存储数据
const state = {
    sum:0,  //当前的和
}

//创建并暴露store
export default new Vuex.Store({
    actions: actions,   //或者直接写actions也行（仅适用于对象中属性和值重名的情况才能简写）
    mutations: mutations,
    state: state,

})
