import axios from 'axios';
export default {
  name: 'Search',
  data: function data() {
    return {
      keyword: ''
    };
  },
  methods: {
    searchUsers: function searchUsers() {
      var _this = this;

      //请求前更新List数据
      this.$bus.$emit('updateListData', {
        isFirst: false,
        isLoading: true,
        errMsg: '',
        users: []
      });
      axios.get("https://api.github.com/search/users?q=".concat(this.keyword)).then(function (response) {
        // 请求成功后更新List数据
        _this.$bus.$emit('updateListData', {
          isFirst: false,
          isLoading: false,
          errMsg: '',
          users: response.data.items
        });
      }, function (error) {
        console.log('请求失败了', error.message); //  this.$bus.$emit('getMessage',error.message)
      });
    }
  }
};
