import axios from 'axios';
export default {
  name: 'Search',
  data: function data() {
    return {
      keyword: ''
    };
  },
  methods: {
    searchUsers: function searchUsers() {
      var _this = this;

      console.log(this); //请求前更新List数据

      this.$bus.$emit('updateListData', {
        isFirst: false,
        isLoading: true,
        errMsg: '',
        users: []
      });
      axios.get("https://api.github.com/search/users?q=".concat(this.keyword)).then(function (response) {
        // 请求成功后更新List数据
        _this.$bus.$emit('updateListData', {
          isLoading: false,
          errMsg: '',
          users: response.data.items
        });
      }, function (error) {
        //  请求失败后更新List数据
        _this.$bus.$emit('updateListData', {
          isLoading: false,
          errMsg: error.message,
          users: []
        });
      });
    }
  }
};
